    """Recursively collect all folder and file paths (including base_path itself)."""
    paths = [base_path]
    for dirpath, dirnames, filenames in os.walk(base_path):
        # Add all subfolders (full path)
        for d in dirnames:
            paths.append(os.path.join(dirpath, d))
        # Add all files (full path)
        for f in filenames:
            paths.append(os.path.join(dirpath, f))
    return paths

