        self.schedule_config = os.path.join(self.project_path, "schedule_config.json")
        self.load_schedule()

    def load_schedule(self):
        if os.path.exists(self.schedule_config):
            with open(self.schedule_config, "r") as f:
                self.tasks = json.load(f)
        else:
            self.tasks = {}

    def save_schedule(self):
        with open(self.schedule_config, "w") as f:
            json.dump(self.tasks, f, indent=4)

    def add_task(self, name, command, time_interval):
        self.tasks[name] = {"command": command, "interval": time_interval}
        self.save_schedule()
        schedule.every(time_interval).seconds.do(self.execute_task, name)
        print(f"Ã¢ÂœÂ… Scheduled: {name} every {time_interval} seconds.")

    def execute_task(self, task_name):
