            subprocess.run(task["command"], shell=True)
            print(f"Ã¢ÂœÂ… Completed: {task_name}")

    def run_scheduler(self):
        print("Ã°ÂŸÂšÂ€ Automation Scheduler Running...")
        while True:
            schedule.run_pending()
            time.sleep(1)

