class RecursivePredictor:
    @staticmethod
    def structured_variation(excretions: List[float], window: int = 10) -> float:
        if not excretions:
            return 0.0
        tail = excretions[-window:]
        return sum(x * (0.85 ** i) for i, x in enumerate(reversed(tail))) / len(tail)

class MemoryCompressor:
    @staticmethod
    def compress(findings: List['AuditResult']) -> str:
        data = ''.join(f"{f.category[0]}{f.severity[0]}{f.message[:8]}" for f in findings)
        hexed = hashlib.sha256(data.encode()).hexdigest()[:12]
        return f"GLYPH-{hexed}"

