class TaskScheduler:
    def __init__(self):
        self.queue: List[Tuple[str, float]] = []  # (task, run_at)
    def schedule(self, intent:str, delay:int=0):
        self.queue.append((intent, time.time()+delay))
    def run_pending(self):
        now_t = time.time()
        for task,run_at in self.queue[:]:
            if run_at <= now_t:
                self.execute(task)
                self.queue.remove((task,run_at))
