class Ledger:
    def __init__(self,path:Path):
        self.path=path
        self.raw=deque(maxlen=KBASE_LIMIT)
        if path.exists():
            self.raw.extend(json.loads(zlib.decompress(path.read_bytes())))
    def append(self,row:dict):
        self.raw.append(row)
        if len(self.raw)>KBASE_LIMIT:
            keep=int(len(self.raw)*DECAY_KEEP)
            self.raw=deque(list(self.raw)[-keep:])
        self.flush()
    def flush(self):
        self.path.write_bytes(zlib.compress(json.dumps(list(self.raw)).encode()))

