        out.append({t: (tf[t]/len(_tokens(d))) * math.log(N/(1+df[t])) for t in tf})
    return out

def _cos(a: dict[str, float], b: dict[str, float]) -> float:
    dot = 0.0; na = 0.0; nb = 0.0
    for k, v in a.items():
