def _tfidf_vecs(docs:List[str])->List[Dict[str,float]]:
    df=defaultdict(int)
    for d in docs:
        for t in set(tokens(d)): df[t]+=1
    N=len(docs)
    vecs=[]
    for d in docs:
        tf=defaultdict(int)
